import React from 'react'

function Toggle() {
    const switchStyle = {
        margin: "30px"
    }

    return (
        <div className="custom-control custom-switch" style={switchStyle}>
            {/* <input type="checkbox" className="custom-control-input" id="customSwitches" />
            <label className="custom-control-label" for="customSwitches">Toggle switch</label> */}


            <label id="date-switch" className="switch">
                <input type="checkbox" checked={props.dateOn} onChange={props.toggleDate} />
                <span className="slider round"></span>
            </label>
        </div>
    )
}

export default Toggle;
