import React, { useContext } from 'react';
import ToggleContext from "./ToggleContext";


function Toggle(props) {
    console.log(useContext(ToggleContext), '****')
    console.log(props, '####')
    const { toggleOn } = useContext(ToggleContext);
    const { setToggleOn } = props;

    function onChange(e) {
        setToggleOn(e.target.checked);
    }

    return (
        <div>
            <label id="date-switch" className="switch">
                <input type="checkbox" checked={toggleOn} onChange={onChange} />
                <span className="slider round"></span>
            </label>

        </div>

    )
}

export default Toggle;
