import React, { useContext } from 'react';
import ToggleContext from "./ToggleContext";


function Toggle() {
    const { value2 } = useContext(ToggleContext);
    const [toggleOn, setToggleOn] = value2;

    function onChange(e) {
        console.log(e.on);
        setToggleOn(false);
    }

    return (
        <div>
            <label id="date-switch" className="switch">
                <input type="checkbox" checked={toggleOn} onChange={onChange} />
                <span className="slider round"></span>
            </label>

        </div>

    )
}

export default Toggle;
