import React from 'react';
import style from '../../src'

function Recipe(props) {
    return (
        <div className={style.recipe}>
            <h1>{props.title}</h1>
            <img alt="pic" src={props.image}></img>
            <p>Calories: {props.calories}</p>
            <ul>
            {props.ingredients.map(item=>
                (
                    <li>{item.text}</li>
                ))}
            </ul>

        </div>
    )
}


export default Recipe;
