    const btnStyle = {
        padding: "20px",

    }

    return (
        <div className="Container" style={containerStyle}>
            <p>{count}</p>
                <button style={btnStyle} onClick={() =>setCount(count+1)}>+</button>
                <button style={btnStyle} onClick={() =>setCount(0)}>Reset</button>
                <button style={btnStyle} onClick={() =>setCount(count-1)}>-</button>
        </div>
    )
}

export default Counter;
