import React, { useContext } from 'react';
import ClockContext from "./ClockContext";


function Toggle() {
    const { date } = useContext(ClockContext);
    const switchStyle = {
        margin: "30px"
    }

    return (
        <div className="custom-control custom-switch" style={switchStyle}>
            <ClockContext.Consumer>
                {value => <div> Date is : {date}</div>}
            </ClockContext.Consumer>

        </div>
    )
}

export default Toggle;
