import React, { useContext } from 'react';
import ToggleContext from "./ToggleContext";


function Display() {

    const { date, toggleOn } = useContext(ToggleContext);


    return (
        <div>
            <p>{date.toLocaleTimeString()}</p>
            {toggleOn &&
                <p>Date is : {date.toString()}</p>
            }

        </div>
    )
}

export default Display;
